create table hh_good_goodinfo
(
    id        int auto_increment
        primary key,
    g_title   varchar(20)   not null,
    g_pic     varchar(100)  not null,
    g_price   decimal(5, 2) not null,
    g_intro   varchar(200)  not null,
    g_stock   int           not null,
    g_desc    longtext      not null,
    is_delete tinyint(1)    not null,
    g_type_id int           not null,
    g_click   int           not null,
    g_unit    varchar(50)   not null,
    constraint hh_good_goodinfo_g_type_id_08ba16a8_fk_hh_good_typeinfo_id
        foreign key (g_type_id) references hh_good_typeinfo (id)
);

INSERT INTO hehe.hh_good_goodinfo (id, g_title, g_pic, g_price, g_intro, g_stock, g_desc, is_delete, g_type_id, g_click, g_unit) VALUES (1, '柠檬', 'hh_good/goods001.jpg', 16.80, '酸柠檬', 200, '<div class="para">柠檬（学名：<em>Citrus limon&nbsp;</em>(L.) Burm. f.），为<a href="https://baike.baidu.com/item/%E5%8F%8C%E5%AD%90%E5%8F%B6%E6%A4%8D%E7%89%A9%E7%BA%B2" target="_blank" rel="noopener">双子叶植物纲</a>芸香科柑橘属植物，柠檬又称<a href="https://baike.baidu.com/item/%E6%9F%A0%E6%9E%9C" target="_blank" rel="noopener">柠果</a>、洋柠檬、<a href="https://baike.baidu.com/item/%E7%9B%8A%E6%AF%8D%E6%9E%9C/3157138" target="_blank" rel="noopener" data-lemmaid="3157138">益母果</a>等。<sup class="sup--normal" data-sup="1" data-ctrmap=":1,">&nbsp;[1]</sup><a class="sup-anchor" name="ref_[1]_5060795"></a>&nbsp;&nbsp;<a href="https://baike.baidu.com/item/%E5%B0%8F%E4%B9%94%E6%9C%A8/3083214" target="_blank" rel="noopener" data-lemmaid="3083214">小乔木</a>，枝少刺或近于无刺，嫩叶及花芽暗紫红色，叶片厚纸质，卵形或椭圆形。单花腋生或少花簇生。果椭圆形或卵形，果皮厚，通常粗糙，柠檬黄色，果汁酸至甚酸，种子小，卵形，端尖；种皮平滑，子叶乳白色，通常单或兼有多胚。花期4-5月，果期9-11月。</div>
<div class="para">柠檬产中国长江以南，<sup class="sup--normal" data-sup="2" data-ctrmap=":2,">&nbsp;[2]</sup><a class="sup-anchor" name="ref_[2]_5060795"></a>&nbsp;&nbsp;原产东南亚，主要产地为美国、意大利、西班牙和<a href="https://baike.baidu.com/item/%E5%B8%8C%E8%85%8A/197766" target="_blank" rel="noopener" data-lemmaid="197766">希腊</a>。<sup class="sup--normal" data-sup="3" data-ctrmap=":3,">&nbsp;[3]</sup><a class="sup-anchor" name="ref_[3]_5060795"></a>&nbsp;</div>
<div class="para">柠檬因其味极酸，孕妇最喜食，故称<a href="https://baike.baidu.com/item/%E7%9B%8A%E6%AF%8D%E6%9E%9C/3157138" target="_blank" rel="noopener" data-lemmaid="3157138">益母果</a>或益母子。柠檬中含有丰富的柠檬酸，因此被誉为&ldquo;柠檬酸仓库&rdquo;。它的果实汁多肉脆，有浓郁的芳香气。因为味道特酸，故只能作为上等调味料，用来调制饮料菜肴、化妆品和药品。<sup class="sup--normal" data-sup="1" data-ctrmap=":1,">&nbsp;[1]</sup><a class="sup-anchor" name="ref_[1]_5060795"></a>&nbsp;&nbsp;此外，柠檬富含<a href="https://baike.baidu.com/item/%E7%BB%B4%E7%94%9F%E7%B4%A0C" target="_blank" rel="noopener">维生素C</a>，能<a href="https://baike.baidu.com/item/%E5%8C%96%E7%97%B0" target="_blank" rel="noopener">化痰</a>止咳，<a href="https://baike.baidu.com/item/%E7%94%9F%E6%B4%A5/10746768" target="_blank" rel="noopener" data-lemmaid="10746768">生津</a><a href="https://baike.baidu.com/item/%E5%81%A5%E8%83%83/97855" target="_blank" rel="noopener" data-lemmaid="97855">健胃</a>。用于<a href="https://baike.baidu.com/item/%E6%94%AF%E6%B0%94%E7%AE%A1%E7%82%8E/982703" target="_blank" rel="noopener" data-lemmaid="982703">支气管炎</a>，<a href="https://baike.baidu.com/item/%E7%99%BE%E6%97%A5%E5%92%B3/727468" target="_blank" rel="noopener" data-lemmaid="727468">百日咳</a>，食欲不振，维生素缺乏，中暑烦渴等症状，它是"<a href="https://baike.baidu.com/item/%E5%9D%8F%E8%A1%80%E7%97%85" target="_blank" rel="noopener">坏血病</a>"的克星。</div>', 0, 1, 0, '500g');
INSERT INTO hehe.hh_good_goodinfo (id, g_title, g_pic, g_price, g_intro, g_stock, g_desc, is_delete, g_type_id, g_click, g_unit) VALUES (2, '红提', 'hh_good/goods002.jpg', 23.00, '新疆提子', 199, '<p>&ldquo;红提&rdquo;葡萄，别名&ldquo;红地球&rdquo;葡萄。于20世纪70年代由美国加利福尼亚大学研究人员经过杂交培育而得。于1987年引入我国栽培，主要种植于陕西、新疆、江苏等地，该品种葡萄品质优，产量高，味道好。</p>', 0, 1, 2, '500g');
INSERT INTO hehe.hh_good_goodinfo (id, g_title, g_pic, g_price, g_intro, g_stock, g_desc, is_delete, g_type_id, g_click, g_unit) VALUES (3, '草莓', 'hh_good/goods003.jpg', 34.00, '大棚草莓', 200, '<div class="para">草莓（英文学名：<a href="https://baike.baidu.com/item/strawberry" target="_blank" rel="noopener">strawberry</a>、拉丁学名：Fragaria &times; ananassa Duch.），多年生<a href="https://baike.baidu.com/item/%E8%8D%89%E6%9C%AC%E6%A4%8D%E7%89%A9" target="_blank" rel="noopener">草本植物</a>。高10-40厘米，茎低于叶或近相等，密被开展黄色柔毛。叶三出，小叶具<a href="https://baike.baidu.com/item/%E7%9F%AD%E6%9F%84/16201076" target="_blank" rel="noopener" data-lemmaid="16201076">短柄</a>，质地较厚，倒卵形或菱形，上面深绿色，几无毛，下面淡白绿色，疏生毛，沿脉较密；<a href="https://baike.baidu.com/item/%E5%8F%B6%E6%9F%84/6728795" target="_blank" rel="noopener" data-lemmaid="6728795">叶柄</a>密被开展黄色柔毛。聚伞花序，花序下面具一短柄的小叶；花两性；萼片卵形，比副萼片稍长；<a href="https://baike.baidu.com/item/%E8%8A%B1%E7%93%A3/74616" target="_blank" rel="noopener" data-lemmaid="74616">花瓣</a>白色，近圆形或倒卵椭圆形。聚合果大，宿存<a href="https://baike.baidu.com/item/%E8%90%BC%E7%89%87/8291114" target="_blank" rel="noopener" data-lemmaid="8291114">萼片</a>直立，紧贴于果实；<a href="https://baike.baidu.com/item/%E7%98%A6%E6%9E%9C/1407632" target="_blank" rel="noopener" data-lemmaid="1407632">瘦果</a>尖卵形，光滑。花期4-5月，果期6-7月<sup class="sup--normal" data-sup="1" data-ctrmap=":1,">&nbsp;[1]</sup><a class="sup-anchor" name="ref_[1]_8032614"></a>&nbsp;&nbsp;。</div>
<div class="para">原产南美，中国各地及欧洲等地广为栽培。草莓营养价值高，含有多种营养物质 ，且有保健功效。</div>', 0, 1, 22, '500g');
INSERT INTO hehe.hh_good_goodinfo (id, g_title, g_pic, g_price, g_intro, g_stock, g_desc, is_delete, g_type_id, g_click, g_unit) VALUES (4, '黄杏', 'hh_good/goods004.jpg', 34.00, '新鲜大黄杏', 200, '<div class="para">杏（学名：<em>Armeniaca vulgaris</em>&nbsp;Lam.）落叶<a href="https://baike.baidu.com/item/%E4%B9%94%E6%9C%A8" target="_blank" rel="noopener">乔木</a>。地生，植株无毛。叶互生，阔卵形或圆卵形叶子，边缘有钝锯齿；近叶柄顶端有二腺体；淡红色花单生或2-3个同生，白色或微红色。圆、长圆或扁圆形<a href="https://baike.baidu.com/item/%E6%A0%B8%E6%9E%9C/5824235" target="_blank" rel="noopener" data-lemmaid="5824235">核果</a>，果皮多为白色、黄色至黄红色，向阳部常具红晕和斑点；暗黄色果肉，味甜多汁；核面平滑没有斑孔，核缘厚而有沟纹。种仁多苦味或甜味。花期3-4月，果期6-7月。</div>
<div class="para">少数地区逸为野生，在新疆伊犁一带野生成纯林或与新疆野苹果林混生，海拔可达3000米。世界各地也均有栽培。杏是重要经济果树树种，是常见<a href="https://baike.baidu.com/item/%E6%B0%B4%E6%9E%9C/6233" target="_blank" rel="noopener" data-lemmaid="6233">水果</a>之一，营养极为丰富，内含较多的糖、蛋白质以及钙、磷等矿物质，另含维生素A原、维生素C和B族维生素等。杏木质地坚硬，是做家具的好材料；杏树枝条可作燃料；杏叶可做饲料。以种子繁育为主，播种时种子需湿沙层积催芽；也可由实生苗作<a href="https://baike.baidu.com/item/%E7%A0%A7%E6%9C%A8/2588400" target="_blank" rel="noopener" data-lemmaid="2588400">砧木</a>作嫁接繁育。</div>', 0, 1, 11, '500g');
INSERT INTO hehe.hh_good_goodinfo (id, g_title, g_pic, g_price, g_intro, g_stock, g_desc, is_delete, g_type_id, g_click, g_unit) VALUES (5, '香蕉', 'hh_good/goods009.jpg', 34.00, '香蕉', 200, '<p>香蕉（学名：<em>Musa nana&nbsp;</em>Lour.）<a href="https://baike.baidu.com/item/%E8%8A%AD%E8%95%89%E7%A7%91" target="_blank" rel="noopener">芭蕉科</a><a href="https://baike.baidu.com/item/%E8%8A%AD%E8%95%89%E5%B1%9E" target="_blank" rel="noopener">芭蕉属</a>植物，又指其<a href="https://baike.baidu.com/item/%E6%9E%9C%E5%AE%9E/320665" target="_blank" rel="noopener" data-lemmaid="320665">果实</a>，<a href="https://baike.baidu.com/item/%E7%83%AD%E5%B8%A6%E5%9C%B0%E5%8C%BA/12603571" target="_blank" rel="noopener" data-lemmaid="12603571">热带地区</a>广泛种植。香蕉味香、富含<a href="https://baike.baidu.com/item/%E8%90%A5%E5%85%BB/815" target="_blank" rel="noopener" data-lemmaid="815">营养</a>，植株为大型<a href="https://baike.baidu.com/item/%E8%8D%89%E6%9C%AC/541696" target="_blank" rel="noopener" data-lemmaid="541696">草本</a>，从根状茎发出，由叶鞘下部形成高3～6米（10～20尺）的假杆；叶长圆形至椭圆形，有的长达3～3.5米（10～11.5尺），宽65厘米（26寸），10～20枚簇生茎顶。穗状花序下垂<sup class="sup--normal" data-sup="1" data-ctrmap=":1,">&nbsp;[1]</sup><a class="sup-anchor" name="ref_[1]_14798168"></a>&nbsp;&nbsp;，由假杆顶端抽出，花多数，淡黄色；果序弯垂，结果10～20串，约50～150个。植株结果后枯死，由根状茎长出的吸根继续<a href="https://baike.baidu.com/item/%E7%B9%81%E6%AE%96/22480" target="_blank" rel="noopener" data-lemmaid="22480">繁殖</a>，每一根株可活多年。原产亚洲东南部，<a href="https://baike.baidu.com/item/%E5%8F%B0%E6%B9%BE/122340" target="_blank" rel="noopener" data-lemmaid="122340">台湾</a>、<a href="https://baike.baidu.com/item/%E6%B5%B7%E5%8D%97/13346" target="_blank" rel="noopener" data-lemmaid="13346">海南</a>、广东、广西等均有栽培。<sup class="sup--normal" data-sup="1" data-ctrmap=":1,:1,">&nbsp;[1]</sup></p>', 0, 1, 60, '500g');
INSERT INTO hehe.hh_good_goodinfo (id, g_title, g_pic, g_price, g_intro, g_stock, g_desc, is_delete, g_type_id, g_click, g_unit) VALUES (6, '大虾', 'hh_good/goods018.jpg', 56.00, '大虾', 200, '<p>虾（Shrimp），是一种<a href="https://baike.baidu.com/item/%E7%94%9F%E6%B4%BB/18684" target="_blank" rel="noopener" data-lemmaid="18684">生活</a>在水中的<a href="https://baike.baidu.com/item/%E8%8A%82%E8%82%A2%E5%8A%A8%E7%89%A9/417366" target="_blank" rel="noopener" data-lemmaid="417366">节肢动物</a>，属<a href="https://baike.baidu.com/item/%E8%8A%82%E8%82%A2%E5%8A%A8%E7%89%A9" target="_blank" rel="noopener">节肢动物</a><a href="https://baike.baidu.com/item/%E7%94%B2%E5%A3%B3%E7%B1%BB" target="_blank" rel="noopener">甲壳类</a>，种类很多<sup class="sup--normal" data-sup="1" data-ctrmap=":1,">&nbsp;[1]</sup><a class="sup-anchor" name="ref_[1]_9861066"></a>&nbsp;&nbsp;，包括南极红虾、<a href="https://baike.baidu.com/item/%E9%9D%92%E8%99%BE/4672816" target="_blank" rel="noopener" data-lemmaid="4672816">青虾</a>、<a href="https://baike.baidu.com/item/%E6%B2%B3%E8%99%BE/10101144" target="_blank" rel="noopener" data-lemmaid="10101144">河虾</a>、<a href="https://baike.baidu.com/item/%E8%8D%89%E8%99%BE/4880361" target="_blank" rel="noopener" data-lemmaid="4880361">草虾</a>、<a href="https://baike.baidu.com/item/%E5%AF%B9%E8%99%BE/417347" target="_blank" rel="noopener" data-lemmaid="417347">对虾</a>、<a href="https://baike.baidu.com/item/%E6%98%8E%E8%99%BE/4883046" target="_blank" rel="noopener" data-lemmaid="4883046">明虾</a>、<a href="https://baike.baidu.com/item/%E9%BE%99%E8%99%BE/569660" target="_blank" rel="noopener" data-lemmaid="569660">龙虾</a>等。 虾具有很高的食疗<a href="https://baike.baidu.com/item/%E8%90%A5%E5%85%BB%E4%BB%B7%E5%80%BC/7997309" target="_blank" rel="noopener" data-lemmaid="7997309">营养价值</a>，可以有蒸、炸等做法，并可以用做于<a href="https://baike.baidu.com/item/%E4%B8%AD%E8%8D%AF%E6%9D%90/15524" target="_blank" rel="noopener" data-lemmaid="15524">中药材</a>。</p>', 0, 2, 4, '500g');
INSERT INTO hehe.hh_good_goodinfo (id, g_title, g_pic, g_price, g_intro, g_stock, g_desc, is_delete, g_type_id, g_click, g_unit) VALUES (7, '扇贝', 'hh_good/goods019.jpg', 89.00, '扇贝', 200, '<p>扇贝属于软体动物门，扇贝科，是我国重要的贝类养殖品种。<sup class="sup--normal" data-sup="1" data-ctrmap=":1,">&nbsp;[1]</sup><a class="sup-anchor" name="ref_[1]_57699"></a>&nbsp;&nbsp;扇贝可食部分的主要营养成分为蛋白质，与鱼类、虾类相似，是一种集食、药、滋补为一体的重要水产食物。<sup class="sup--normal" data-sup="2" data-ctrmap=":2,">&nbsp;[2]</sup><a class="sup-anchor" name="ref_[2]_57699"></a>&nbsp;&nbsp;扇贝广泛分布于世界各海域。<sup class="sup--normal" data-sup="3" data-ctrmap=":3,">&nbsp;[3]</sup><a class="sup-anchor" name="ref_[3]_57699"></a>&nbsp;&nbsp;扇贝柱的干制品称为&ldquo;干贝&rdquo;，为名贵海珍品。<sup class="sup--normal" data-sup="4" data-ctrmap=":4,">&nbsp;[4]</sup></p>', 0, 6, 3, '500g');
INSERT INTO hehe.hh_good_goodinfo (id, g_title, g_pic, g_price, g_intro, g_stock, g_desc, is_delete, g_type_id, g_click, g_unit) VALUES (8, '猪肉', 'hh_good/96dda144ad345982e516df8c04f431adcbef840b.jpeg', 57.00, '猪肉', 200, '<div class="para">猪肉，又名<a href="https://baike.baidu.com/item/%E8%B1%9A%E8%82%89/5560604" target="_blank" rel="noopener" data-lemmaid="5560604">豚肉</a>，是<a href="https://baike.baidu.com/item/%E7%8C%AA%E7%A7%91" target="_blank" rel="noopener">猪科</a>动物<a href="https://baike.baidu.com/item/%E5%AE%B6%E7%8C%AA/1730315" target="_blank" rel="noopener" data-lemmaid="1730315">家猪</a>的肉。其性味甘咸平，含有丰富的<a href="https://baike.baidu.com/item/%E8%9B%8B%E7%99%BD%E8%B4%A8/309120" target="_blank" rel="noopener" data-lemmaid="309120">蛋白质</a>及<a href="https://baike.baidu.com/item/%E8%84%82%E8%82%AA/85926" target="_blank" rel="noopener" data-lemmaid="85926">脂肪</a>、<a href="https://baike.baidu.com/item/%E7%A2%B3%E6%B0%B4%E5%8C%96%E5%90%88%E7%89%A9/88328" target="_blank" rel="noopener" data-lemmaid="88328">碳水化合物</a>、<a href="https://baike.baidu.com/item/%E9%92%99" target="_blank" rel="noopener">钙</a>、<a href="https://baike.baidu.com/item/%E9%93%81/29586" target="_blank" rel="noopener" data-lemmaid="29586">铁</a>、<a href="https://baike.baidu.com/item/%E7%A3%B7" target="_blank" rel="noopener">磷</a>等营养成分&nbsp;<sup class="sup--normal" data-sup="1" data-ctrmap=":1,">[1]</sup><a class="sup-anchor" name="ref_[1]_18473369"></a>&nbsp;&nbsp;。猪肉是日常生活的主要副食品，具有补虚强身，滋阴润燥、丰肌泽肤的作用。凡病后体弱、产后血虚、面黄肌瘦者，皆可用之作营养滋补之品。</div>
<div class="para">猪肉作为餐桌上重要的<a href="https://baike.baidu.com/item/%E5%8A%A8%E7%89%A9%E6%80%A7%E9%A3%9F%E5%93%81/9668197" target="_blank" rel="noopener" data-lemmaid="9668197">动物性食品</a>之一，因为<a href="https://baike.baidu.com/item/%E7%BA%A4%E7%BB%B4/3242959" target="_blank" rel="noopener" data-lemmaid="3242959">纤维</a>较为细软，<a href="https://baike.baidu.com/item/%E7%BB%93%E7%BC%94%E7%BB%84%E7%BB%87/3233435" target="_blank" rel="noopener" data-lemmaid="3233435">结缔组织</a>较少，肌肉组织中含有较多的肌间脂肪，因此，经过烹调加工后肉味特别鲜美。</div>
<div class="para">2019年8月21日，召开的国务院常务会议就稳定生猪生产和猪肉保供稳价提出了一系列要求，&ldquo;不敢养&rdquo;&ldquo;不让养&rdquo;&ldquo;没地养&rdquo;&ldquo;没钱养&rdquo;的症结正在被打破。通过政策引导和生产扶持，预计2020年生猪生产将逐步恢复，生猪价格将逐步回落<sup class="sup--normal" data-sup="2" data-ctrmap=":2,">&nbsp;[2]</sup><a class="sup-anchor" name="ref_[2]_18473369"></a>&nbsp;&nbsp;。</div>
<div class="para">2020年4月，猪肉价格重回&ldquo;20元时代&rdquo;。<sup class="sup--normal" data-sup="3" data-ctrmap=":3,">&nbsp;[3]</sup></div>', 0, 3, 1, '500g');